import { useEffect, useState} from 'react'

import ProductBox from '../components/ProductBox'
import AddProduct from '../components/AddProduct'
import EditProduct from '../components/EditProduct'

const Home = () => {

    const [products, setProducts] = useState(null)
    const [showModal, setShowModeal] = useState(null)
    const [selectedProductId, setSelectedProductId] = useState(null)

    const fetchProducts = async () => {
        const response = await fetch('/api/product');
        const json = await response.json();

        if (response.ok) {
            setProducts(json); 
        }
    };

    useEffect(() => {
        fetchProducts();
    }, []);

    const openEditModal = (productId) => {
        setShowModeal(true);
        setSelectedProductId(productId);
    }

    const hideModal = () => {
        setShowModeal(false);
        setSelectedProductId(null);
    }

    return(
        <div className="home">
            <div className="products">
                {products && products.map((product) => (
                    <ProductBox key={product._id} product={product} fetchProducts={fetchProducts}/>
                ))}
            </div>
            <AddProduct fetchProducts={fetchProducts}/>
            {showModal && (
                <div className="modal">
                    <div className="modal-content">
                        <button className="close" onClick={closeModal}>X</button>
                        <EditProduct
                            productId={selectedProductId} // Pass productId to EditProduct
                            fetchProducts={fetchProducts} // Optional, if you want to refresh after update
                            closeModal={closeModal} // Close modal after edit
                        />
                    </div>
                </div>
            )}
        </div>
    );
};

export default Home;
