import { useEffect,useState } from "react"

const EditProduct = ({ productId, closeModal, fetchProducts }) => {
    const [product, setProduct] = useState(null);


    useEffect(() => {
    const fetchProduct = async (id) => {
        const response = await fetch(`/api/product/${id}`);
        const json = await response.json();

        if (response.ok) {
            setProduct(json); 
        }
    };
        if (productId) {
            fetchProduct();
        }
    }, [productId]);

    const updateProduct = async (e) => {
        e.preventDefault();
        const response = await fetch(`/api/product/${product._id}`, {
            method: 'PUT',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({
                name: e.target.name.value,
                description: e.target.description.value
            })
        })

        if (response.ok) {
            console.log('Product updated successfully');
            fetchProducts();
            // or redirect to another page
        } else {
            console.error('Failed to update product');
        }
    }

    return (
        <form onSubmit={updateProduct}>
            <input type="text" defaultValue={product?.name} placeholder="Product Name" name="name" />
            <input type="text" defaultValue={product?.description} placeholder="Product Description" name="description" />
            <button type="submit">Update Product</button>
        </form>
    )
}

export default EditProduct;
